import React from 'react';
import '../styles/RPFilter.css';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css'; 

const { createSliderWithTooltip } = Slider;
const Range = createSliderWithTooltip(Slider.Range);

const Filter = ({ filters, onFilterChange }) => {


  const handlePriceRangeChange = (value) => {
    onFilterChange('priceRange', { min: value[0], max: value[1] });

};
  return (
    <div className= "filterContainer">
      <h3>Filters</h3>
      <div className = "singleFilter">
        <label>
          <input type="checkbox" name="onCampus" onChange={handleFilterChange} />
          On-Campus Housing
        </label>
      </div>
      <div className = "singleFilter">
        <label>
          <input type="checkbox" name="freshman" onChange={handleFilterChange} />
          Freshman Housing
        </label>
      </div>
      <div className={styles.priceRange}>
        <label>Price Range</label>
        <Range
          min={0}
          max={5000}
          value={[filters.priceRange.min, filters.priceRange.max]}
          onChange={handlePriceRangeChange}
          tipFormatter={(value) => `$${value}`}
          trackStyle={[{ backgroundColor: 'blue' }]}
          handleStyle={[{ borderColor: 'blue' }, { borderColor: 'blue' }]}
          railStyle={{ backgroundColor: 'grey' }}
        />
        <div>
          Min: ${filters.priceRange.min} | Max: ${filters.priceRange.max}
        </div>
      </div>
      <div className="checkboxGroup">
        <h4>Room Types</h4>
        {['studio', '1 bedroom', '2 bedroom', 'triple', 'quad'].map((type) => (
          <label key={type} className = "roomType">
            <input type="checkbox" name={type} onChange={handleFilterChange} />
            {type}
          </label>
        ))}
      </div>
    </div>
  );
};

export default Filter;
