// create posting page
// allow user to insert title, content and choose housing type and category

import React from 'react';
import { useState } from 'react';
import '../App.css';
import { Button, TextField, Stack } from '@mui/material';

export default function FBPostPage() {
    const [housingType, setHousingType] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [category, setCategory] = useState('');

    const handleHousingTypeClick = (event) => {
        setHousingType(event.target.value);
    };



    return (
        <div className="main">
                <Stack direction="row" spacing={2}>
                    <Button variant="outlined" value = "On Campus" 
                        color="success" onClick={handleHousingTypeClick}>
                        On Campus</Button>
                    <Button variant="outlined" value = "Off Campus"
                        color="success" onClick={handleHousingTypeClick}>
                        Off Campus</Button>
                </Stack>
                <h2 className='postEle'>Title</h2>
                <TextField id="outlined-basic" variant="outlined" color = "success"/>
                <h2 className='postEle'>Content</h2>
                <TextField id="outlined-basic" variant="outlined" color = "success"/>
                <h2 className='postEle'>Category</h2>

        </div>
    );
}
