export default function LikeButton( {likes, pid} ) {
    const [selected, setSelected] = useState(false);

    const handleLike = (event) => {
        setSelected(!selected);
        updateLike(likes, pid);
    }
    return (
        <div>
            <span className='postButtom'>
                <a href="#" onClick={handleLike}>
