import { useState } from "react";
import usePagination from '../utils/usePagination';
import { Box, Divider, List, Pagination } from "@mui/material";
import LikeButtom from './FBLikeButtom';
// import CommentButtom from './FBCommentButtom';
import NotifyButtom from './FBNotifyButtom';
import CommentIcon from '@mui/icons-material/Comment';


export default function PostList( {data} ) {
  const [page, setPage] = useState(1);
  const [showComments, setShowComments] = useState(false);

  const PER_PAGE = 4;

  const count = Math.ceil(data.length / PER_PAGE);
  const _DATA = usePagination(data, PER_PAGE);

  const handleChange = (e, p) => {
    setPage(p);
    _DATA.jump(p);
  };

  const handleShowComments = (e) => {
    setShowComments(!showComments);
  }

  function CommentButtom( {comments, key} ) {
    const handleComment = (event) => {
      setShowComments(!showComments);
    }

    return (
      <div>
        <span className='postButtom'>
          <a href="#" onClick={handleComment}>
              <CommentIcon fontSize="medium" data-testid="comment-icon"/>
          </a>
          &nbsp;
          {comments.length}
        </span>
        {showComments && (
          <div>
            {/* display comments here */}
            <List>
              {comments}
            </List>
          </div>
        )}
      </div>
    );

  }


  function PostEntry( {key, title, content, likes, comments} ) { // add an ID field
    return (
      <div className='postEntry'>
        <h3>{title}</h3>
        <p>{content}</p>
        <div className='postEle'>
          <LikeButtom likes={likes} pid={key} />
          <CommentButtom comments={comments} key={key}/>
          <NotifyButtom />
        </div>        
      </div>
    )

  }


  return (
    <Box p="5" data-testid="post-list-box">
      <List>
        {_DATA.currentData().map(v => {
         // return (
          <div key = {v.pid}> 
            <PostEntry 
              key={v.pid}
              title={v.title} 
              content={v.content} 
              likes={v.likes} 
              comments={v.comments}/>
              <Divider />
          </div>
       //   );
        })}
      </List>
      <Pagination
        count={count}
        size="large"
        page={page}
        variant="outlined"
        shape="rounded"
        onChange={handleChange}
      />
    </Box>
  );
}
