import Filter from './RPFilter.js';
import Preference from './RPPreference.js';
import HousingList from './RPHousingList.js';
import React, { useState } from 'react';
import FBHeader from './FBHeader.js';

function RPMain() {



  const username = 'JohnDoe';
  const posts = ['First post', 'Second post', 'Third post'];

  const [preferences, setPreferences] = useState([
    { name: 'security', label: 'Security', value: 0 },
    { name: 'distance', label: 'Distance to Campus', value: 0 },
    { name: 'amenity', label: 'Amenity', value: 0 },
  ]);

  const handlePreferenceChange = (name, value) => {
    setPreferences(
      preferences.map((pref) => (pref.name === name ? { ...pref, value } : pref))
    );
  };


  const [filters, setFilters] = useState({
    onCampus: false,
    freshman: false,
    priceRange: [0, 1000],
    roomTypes: [],
  });

  const handleFilterChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  return (
      <div>
        <FBHeader title="Forum Board"/>
        <div className = "RPLeft">
            <Filter filters={filters} onFilterChange={handleFilterChange} />
            <Preference preferences={preferences} onPreferenceChange={handlePreferenceChange} />
        </div>



      </div>


  )
}

export default RPMain;
