import axios from 'axios';
import { rootURL } from "../utils/utils";

// use try catch to handle error
export const getOneThread = async (postId) => {
    try {
        const res = await axios.get(`${rootURL}/posts?pid=${postId}`);
        // console.log("get one thread", res.data);
        return res.data;
    } catch (err) {
        console.error("error", err.message);
    }   
}
