import { getAllCommentsByPostId, createComment, updateCommentLength, updateCommentLike } from '../api/FBMainAPI';
import { useEffect, useState } from 'react';
import { TextField, Button } from '@mui/material';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';


export default function CommentSection( {pid, comments} ) {
    console.log("comments", pid);
    const [commentsData, setCommentsData] = useState([]);
    const [newComment, setNewComment] = useState("");
    const [selected, setSelected] = useState(false);

    useEffect(() => {
      async function getAllCommentsByPostIdWrapper() {
        const commentsData = await getAllCommentsByPostId(pid);
        setCommentsData(commentsData);
      }
      getAllCommentsByPostIdWrapper();
    }, [newComment, selected]);

    const handleChangeContent = (event) => {
      setNewComment(event.target.value);
    }

    const handleCreateComment = () => {
      createComment(pid, newComment);
      setNewComment("");
      updateCommentLength(pid, comments + 1);

    }

    function LikeButton( {likes, cid} ) {
      const handleLike = (event) => {
          setSelected(!selected);
          if (selected) {
              updateCommentLike(likes + 1, cid);
          } else {
              updateCommentLike(likes, cid);
          }

      }

      return (
        <div>
          <span className='postButtom'>
            <a href="#" onClick={handleLike}>
              {!selected && (
              <FavoriteBorderIcon fontSize="medium" data-testid="like-buttom-icon"/>
              )}
              {selected && (
              <FavoriteIcon fontSize="medium" data-testid="like-buttom-icon"/>
              )}
            </a>
            {likes + selected}
          </span>
        </div>
      );
    }

    return (
      <div>
        <div className='writeComment'>
          <TextField 
            style={{
                borderRadius: 35,
                width: "87%",
                color: "rgba(117, 137, 122, 0.7)",
                padding: "10px 0px",
            }}
            size="small"
            id="full-width-text-field"
            variant="outlined" 
            color = "success"
            data-testid="content"
            placeholder = "Share your thoughts..."
            multiline
            rows={1}
            value={newComment}
            onChange = {handleChangeContent}
            />
            <div>
              &nbsp;
              &nbsp;
              &nbsp;
              &nbsp;
            </div>
            <Button 
            variant="contained" 
            onClick={handleCreateComment}>
              Post</Button>

        </div>

        <div className='commentSection'>
          {commentsData.map(comment => (
            <div key={comment.cid} className='oneComment'>
              <p>{comment.content}</p>
              <LikeButton likes={comment.likes} cid={comment.cid} />
            </div>
          ))}
        </div>
      </div>
    )
}
