// create posting page
// allow user to insert title, content and choose housing type and category

import React from 'react';
import { useState } from 'react';
import '../App.css';
import { Button, TextField, Stack } from '@mui/material';

export default function FBPostPage() {
    const [currentOnCampus, setcCurrentOnCampus] = useState('outlined');
    const [housingType, setHousingType] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [category, setCategory] = useState('');

    const handleHousingTypeClickOne = (event) => {
        if (currentButtonVariant === 'outlined') {
            setCurrentButtonVariant('contained');
        }
        else {
            setCurrentButtonVariant('outlined');
        }
        setHousingType(event.target.value);
    };

    const handleHousingTypeClickTwo = (event) => {
        if (currentButtonVariant === 'outlined') {
            setCurrentButtonVariant('contained');
        }
        else {
            setCurrentButtonVariant('outlined');
        }
        setHousingType(event.target.value);
    };

    const handleChangeTitle = (event) => {
        setTitle(event.target.value);
    };

    const handleChangeContent = (event) => {
        setContent(event.target.value);
    };

    const handleChangeCategory = (event) => {
        setCategory(event.target.value);
    };

    const handleChangePosting = (event) => {
        event.preventDefault();
    };



    return (
        <div className="main">
            <div>
                <h1>Post a Thread</h1>
                <Stack direction="row" spacing={2}>
                    <Button 
                        style={{
                            borderRadius: 65,
                            padding: "12px 62.5px",
                            fontSize: "18px"
                        }}
                        color = "success"
                        variant={currentOnCampus}
                        value = "On Campus" 
                        onClick={handleHousingTypeClickOne}>
                        On Campus Housing</Button>
                    <Button 
                        style={{
                            borderRadius: 65,
                            padding: "12px 62.5px",
                            fontSize: "18px"
                        }}
                        color = "success"
                        variant={currentOffCampus}
                        value = "Off Campus"
                        onClick={handleHousingTypeClickTwo}>
                        Off Campus Housing</Button>
                </Stack>
                <h2 className='postEle'>Title</h2>
                <TextField 
                    id="outlined-basic" 
                    variant="outlined" 
                    color = "success"
                    placeholder = "Enter your label here"
                    multiline
                    rows={1}
                    maxRows={2}
                    value={title}
                    onChange = {handleChangeTitle}/>
                <h2 className='postEle'>Category</h2>

                <h2 className='postEle'>Content</h2>
                <TextField 
                    id="outlined-basic" 
                    variant="outlined" 
                    color = "success"
                    placeholder = "Enter you text here"
                    multiline
                    rows={10}
                    maxRows={10}
                    value={content}
                    onChange = {handleChangeContent}
                    />
                <Button
                    style={{
                        borderRadius: 65,
                        padding: "12px 62.5px",
                        fontSize: "18px"
                    }}
                    variant="contained"
                    onClick={handleChangePosting}>
                        Post</Button>


            </div>
        </div>
    );
}
