import FBCommentLikeButtom from './FBCommentLikeButtom'
import { getOneComment } from '../api/FBMainAPI';

export default function CommentSection( {pid, comments} ) {
    async function getOneCommentWrapper(cid) {
        return await getOneComment(cid);
    }

    return (
      <div className='commentSection'>
        <h4>Comments</h4>
        <div>
            {comments.currentData().map((v, i) => {
                return (
                <div key={i} className='oneComment'>
                    <FBCommentLikeButtom likes={getOneCommentWrapper(v).likes} cid={v} />
                    <p>{getOneCommentWrapper(v).content}</p>
                </div>
                )
            })}
        </div>
      </div>
    )
  }
