import { useState } from "react";
import usePagination from '../utils/usePagination';
import { Box, List, Pagination } from "@mui/material";
import LikeButtom from './FBLikeButtom';
import CommentButtom from './FBCommentButtom';
import NotifyButtom from './FBNotifyButtom';

export default function PostList( {data} ) {
  const [page, setPage] = useState(1);
  const [showcomments, setShowComments] = useState(false);

  const PER_PAGE = 4;

  const count = Math.ceil(data.length / PER_PAGE);
  const _DATA = usePagination(data, PER_PAGE);

  const handleChange = (e, p) => {
    setPage(p);
    _DATA.jump(p);
  };


  function PostEntry( {key, title, content, likes, commentLength} ) { // add an ID field


      return (
        <div className='postEntry'>
          <h3>{title}</h3>
          <p>{content}</p>
          <div className='postEle'>
            <LikeButtom likes={likes} pid={key} />

            {/* conditional rendering */}
            <CommentButtom comments={commentLength} key={key}/>

            {/* <details>
              <summary>

              </summary>
              <p>Post Content goes here</p>
            </details> */}

            <NotifyButtom />
          </div>        
        </div>
      )
  }


  return (
    <Box p="5" data-testid="post-list-box">
      <List>
        {_DATA.currentData().map(v => {
          return (
            <PostEntry 
              key={v.pid}
              title={v.title} 
              content={v.content} 
              likes={v.likes} 
              commentLength={v.comments_length}/>

          );
        })}
      </List>
      <Pagination
        count={count}
        size="large"
        page={page}
        variant="outlined"
        shape="rounded"
        onChange={handleChange}
      />
    </Box>
  );
}
