import React from 'react';
import styles from '../styles/RPFilter.css';

const Filter = ({ onFilterChange }) => {
  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    onFilterChange(name, type === 'checkbox' ? checked : value);
  };

  return (
    <div className={styles.filterContainer}>
      <h3>Filters</h3>
      <div>
        <label>
          <input type="checkbox" name="onCampus" onChange={handleFilterChange} />
          On-Campus Housing
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" name="freshman" onChange={handleFilterChange} />
          Freshman Housing
        </label>
      </div>
      <div>
        <label>
          Price Range: <input type="range" name="price" min="0" max="5000" onChange={handleFilterChange} />
        </label>
      </div>
      <div className={styles.checkboxGroup}>
        <h4>Room Types</h4>
        {['studio', '1 bedroom', '2 bedroom', 'triple', 'quad'].map((type) => (
          <label key={type}>
            <input type="checkbox" name={type} onChange={handleFilterChange} />
            {type}
          </label>
        ))}
      </div>
    </div>
  );
};

export default Filter;
