import React, { useState, useEffect } from 'react';
import { getOneThread } from '../api/FBThreadAPI';

export default function FBOneThread({postId}) {
    const [post, setPost] = useState({});
    const [comments, setComments] = useState([]);

    useEffect(() => {
        async function getOneThreadWrapper() {
            const data = await getOneThread(postId);
            setPost(data);
        }
        getOneThreadWrapper();
    }, []);

    handleBacktoFB = () => {
        window.location.href = "/forum-board";
    }

    return (

        <div>
            <Button 
            variant="contained" 
            onClick={handleBacktoFB}>
                Back to Forum Board</Button>
            <h1>{post.title}</h1>
            <p>{post.content}</p>
        </div>
    )

}
