// create posting page
// allow user to insert title, content and choose housing type and category

import React from 'react';
import { useState } from 'react';
import '../App.css';
import { Button, TextField, Stack } from '@mui/material';

export default function FBPostPage() {
    const [housingType, setHousingType] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [category, setCategory] = useState('');

    const handleHousingTypeClick = (event) => {
        setHousingType(event.target.value);
    };



    return (
        <div className="main">
            <div>
                <h1>Post a Thread</h1>
                <Stack direction="row" spacing={2}>
                    <Button 
                        style={{
                            borderRadius: 65,
                            padding: "12px 62.5px",
                            fontSize: "18px"
                        }}
                        variant="outlined" 
                        value = "On Campus" 
                        color="success" 
                        onClick={handleHousingTypeClick}>
                        On Campus</Button>
                    <Button 
                        style={{
                            borderRadius: 65,
                            padding: "12px 62.5px",
                            fontSize: "18px"
                        }}
                        variant="outlined" 
                        value = "Off Campus"
                        color="success" 
                        onClick={handleHousingTypeClick}>
                        Off Campus </Button>
                </Stack>
                <h2 className='postEle'>Title</h2>
                <TextField 
                    id="outlined-basic" 
                    variant="outlined" 
                    color = "success"
                    placeholder = "Enter your label here"
                    multiline
                    rows={1}
                    maxRows={2}/>
                <h2 className='postEle'>Category</h2>

                <h2 className='postEle'>Content</h2>
                <TextField 
                    id="outlined-basic" 
                    variant="outlined" 
                    color = "success"
                    placeholder = "Enter you text here"
                    multiline
                    rows={10}
                    maxRows={10}/>


            </div>
        </div>
    );
}
