import FBCommentLikeButton from './FBCommentLikeButton'
import { getOneComment } from '../api/FBMainAPI';
import { useEffect, useState } from 'react';

export default function CommentSection( {comments} ) {
    const [commentsContent, setCommentsContent] = useState([]);

    useEffect(() => {
      async function fetchCommentData() {
        const data = [];
        for (const commentId of comments) {
          const comment = await getOneComment(commentId).content;
          data.push(comment);
        }
        setCommentsData(data);
      }
      fetchCommentData();
    }, [comments]);


    return (
      <div className='commentSection'>
        <h4>Comments</h4>
        <div>
          {commentsData.map((comment, i) => (
            <div key={i} className='oneComment'>
              <FBCommentLikeButton likes={comment.likes} cid={comment.id} />
              <p>{comment.content}</p>
            </div>
          ))}
        </div>
      </div>
    )
  }
